import React from 'react';
import { Button } from '@/components/ui/button';

export default function BookList({ kitabs, onSelectPasal }) {
  return (
    <div>
      <h2 className="text-lg font-semibold mb-4">Pilih Kitab dan Pasal</h2>
      <div className="grid grid-cols-2 sm:grid-cols-3 gap-3">
        {kitabs.map((kitab) => (
          <div key={kitab.book} className="border p-3 rounded-lg shadow-sm dark:border-gray-700 dark:bg-gray-800">
            <h3 className="font-bold mb-2 text-center">{kitab.abbr}</h3>
            <div className="flex flex-wrap justify-center gap-2">
              {kitab.chapters.map((chap) => (
                <Button
                  key={chap}
                  size="sm"
                  variant="outline"
                  onClick={() => onSelectPasal(kitab.book, chap)}
                >
                  {chap}
                </Button>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
