import React, { useState } from 'react';
import Header from './components/Header';
import BookList from './components/BookList';
import VerseList from './components/VerseList'; // jika ada
import './index.css';

const App = () => {
  const [selectedBook, setSelectedBook] = useState(null);

  return (
    <div className="min-h-screen bg-white dark:bg-gray-900 text-gray-900 dark:text-gray-100 transition-colors">
      <Header />
      
      {!selectedBook ? (
        <BookList onSelectBook={setSelectedBook} />
      ) : (
        <div className="p-4 space-y-4">
          <h2 className="text-xl font-bold">Kitab: {selectedBook}</h2>
          {/* Tempatkan komponen PasalList atau VerseList di sini */}
          {/* Contoh: <VerseList book={selectedBook} /> */}
          <button
            onClick={() => setSelectedBook(null)}
            className="px-4 py-2 mt-4 text-sm bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Kembali ke daftar kitab
          </button>
        </div>
      )}
    </div>
  );
};

export default App;
